import { Component, Inject } from '@angular/core';
import { MAT_DIALOG_DATA, MatDialogRef } from '@angular/material/dialog';
import { Card } from '../../models/card.model';

@Component({
  selector: 'app-edit-modal',
  templateUrl: './edit-modal.component.html',
  styleUrls: ['./edit-modal.component.css']
})
export class EditModalComponent {
  title: string;
  description: string;
  isEdit: boolean;

  constructor(
    public dialogRef: MatDialogRef<EditModalComponent>,
    @Inject(MAT_DIALOG_DATA) public data: Card
  ) {
    this.isEdit = !!data.id;
    this.title = data.title || '';
    this.description = data.description || '';
  }

  save() {
    if (this.title.trim() && this.description.trim()) {
      this.dialogRef.close({ ...this.data, title: this.title, description: this.description });
    }
  }

  cancel() {
    this.dialogRef.close();
  }
}
<h2 mat-dialog-title>{{ isEdit ? 'Edit Card' : 'Add New Card' }}</h2>
<mat-dialog-content>
  <mat-form-field appearance="outline" class="full-width">
    <mat-label>Title</mat-label>
    <input matInput [(ngModel)]="title" required>
  </mat-form-field>
  <mat-form-field appearance="outline" class="full-width">
    <mat-label>Description</mat-label>
    <textarea matInput [(ngModel)]="description" rows="4"></textarea>
  </mat-form-field>
</mat-dialog-content>

<mat-dialog-actions align="end">
  <button mat-button (click)="cancel()">Cancel</button>
  <button mat-flat-button color="primary" (click)="save()">{{ isEdit ? 'Save' : 'Add' }}</button>
</mat-dialog-actions>
